machine_end_gcode:
G4 ; wait

{if max_layer_z < max_print_height}G1 Z{z_offset+min(max_layer_z+5, max_print_height)}{endif} ; Move print head up
P0 S1 ; park tool
M84 ; disable motors

; turn off extruder heaters
{if is_extruder_used[0]} M104 T0 S0 {endif}
{if is_extruder_used[1]} M104 T1 S0 {endif}
{if is_extruder_used[2]} M104 T2 S0 {endif}
{if is_extruder_used[3]} M104 T3 S0 {endif}
{if is_extruder_used[4]} M104 T4 S0 {endif}

M140 S0 ; turn off heatbed
M107 ; turn off fan
M221 S100 ; reset flow percentage
M84 ; disable motors
M77 ; stop print timer
; max_layer_z = [max_layer_z]

change_filament_gcode:
; Change Tool[previous_extruder] -> Tool[next_extruder] (layer [layer_num])
{
local max_speed_toolchange = 350.0;
local wait_for_extruder_temp = true;
position[2] = position[2] + 2.0;

local speed_toolchange = max_speed_toolchange;
if travel_speed < max_speed_toolchange then
      speed_toolchange = travel_speed;
endif
"G1 F" + (speed_toolchange * 60) + "
";
if wait_for_extruder_temp and not((layer_num < 0) and (next_extruder == initial_tool)) then
      "P0 S1 L2 D0
";
      "; " + layer_num + "
";
      if layer_num == 0 then
            "M109 S" + first_layer_temperature[next_extruder] + " T" + next_extruder + "
";
      else
            "M109 S" + temperature[next_extruder] + " T" + next_extruder + "
";
      endif
endif
"T" + next_extruder + " S1 L0 D0
";
}
