;END Gcode for filament T[current_extruder] 
G92 E0
G1 F600 E-6 ;retract
{if current_extruder == 0}G0 X-20 F12000  {endif} ;
{if current_extruder == 1}G0 X350 F12000  {endif} ;
M106 S0 ; Lüfter aus
{if has_wipe_tower and total_toolchanges > 0 and current_extruder == 0}T1 
G0 X{wipe_tower_x[0]} Y{wipe_tower_y[0]} F{travel_speed*60}{endif} ;
{if has_wipe_tower and total_toolchanges > 0 and current_extruder == 1}T0 
G0 X{wipe_tower_x[0]} Y{wipe_tower_y[0]} F{travel_speed*60}{endif} ;
